import { OrbitControls, useTexture, shaderMaterial } from "@react-three/drei";
import * as THREE from "three";
import { TextureLoader } from "three/src/loaders/TextureLoader";
import starVertexShader from "./shaders/starVertex.js";
import starFragmentShader from "./shaders/starFragment.js";
import waveVertexShader from "./shaders/waveVertex.js";
import waveFragmentShader from "./shaders/waveFragment.js";
import { extend, useFrame } from "@react-three/fiber";
import { useRef } from "react";
import { useControls } from "leva";

let depthColor = new THREE.Color("#cc2965");
let surfaceColor = new THREE.Color("#07f5ae");

const StarMaterial = shaderMaterial({}, starVertexShader, starFragmentShader);

const WaveMaterial = shaderMaterial(
  {
    uTime: 0,
    uBigWavesElevation: 0.2,
    uBigWavesFrequency: new THREE.Vector2(4, 1.5),
    uBigWavesSpeed: 0.75,
    uSmallWavesElevation: 0.15,
    uSmallWavesFrequency: 3,
    uSmallWavesSpeed: 0.2,
    uSmallIterations: 4,
    uDepthColor: color,
    uSurfaceColor: new THREE.Color(surfaceColor),
    uColorOffset: 0.08,
    uColorMultiplier: 5,
  },
  waveVertexShader,
  waveFragmentShader
);

let lat = 15.6677 * (Math.PI / 180);
let lng = 96.5545 * (Math.PI / 180);

let x = Math.cos(lng) * Math.sin(lat) * 2;
let y = Math.sin(lng) * Math.sin(lat) * 2;
let z = Math.cos(lat) * 2;

extend({ StarMaterial, WaveMaterial });

function getRandom(min, max) {
  return Math.random() * (max - min) + min;
}

export default function Experience() {
  const props = useTexture({ map: "earthMap.jpg" });

  const { radius, tube, rotation } = useControls("geometry", {
    radius: {
      value: 0.09,
      min: 0,
      max: 1,
      step: 0.01,
    },
    tube: {
      value: 0.02,
      min: 0,
      max: 0.1,
      step: 0.01,
    },
    rotation: {
      value: 0,
      min: -Math.PI * 2,
      max: Math.PI * 2,
      step: 0.1,
    },
  });

  const { color } = useControls("shader", {
    color: depthColor,
  });

  useFrame(({ clock }) => (ref.current.uTime = clock.getElapsedTime()));

  const ref = useRef();

  return (
    <>
      <OrbitControls />

      <ambientLight intensity={0.2} />
      <directionalLight />
      <mesh>
        <sphereGeometry args={[2, 32, 32]} />
        <meshStandardMaterial {...props} />
      </mesh>
      <mesh position={[x, y, z]} rotation={[rotation, 0, 0]}>
        <torusGeometry args={[radius, tube, 16, 24, Math.PI * 2]} />
        <waveMaterial ref={ref} uDepthColor={color} />
      </mesh>

      {[...Array(400)].map(() => (
        <mesh
          position={[
            (Math.random() - 0.5) * 50,
            (Math.random() - 0.5) * 50,
            (Math.random() - 0.5) * 50,
          ]}
          rotation={[
            getRandom(0, Math.PI * 2),
            getRandom(0, Math.PI * 2),
            getRandom(0, Math.PI * 2),
          ]}
        >
          <sphereGeometry args={[0.05, 32, 32]} />
          <starMaterial />
        </mesh>
      ))}
    </>
  );
}
