export default `
varying vec2 vUv;
uniform float uTime;
#define PI 3.1415926535897932384626433832795

void main()
{

    vec2 wavedUv = vec2(
        vUv.x + sin(vUv.y * 100.0) 0,5,
        vUv.y + sin(vUv.x * 100.0) * 0.5
    );
    float strength = 1.0 - step(0.01, abs(distance(wavedUv, vec2(0.5)) - 0.25));

    gl_FragColor = vec4(vec3(strength), 1.0);
}`;
