import { OrbitControls, useTexture, shaderMaterial } from "@react-three/drei";
import * as THREE from "three";
import { TextureLoader } from "three/src/loaders/TextureLoader";
import starVertexShader from "./shaders/starVertex.js";
import starFragmentShader from "./shaders/starFragment.js";
import waveVertexShader from "./shaders/waveVertex.js";
import waveFragmentShader from "./shaders/waveFragment.js";
import { extend, useFrame } from "@react-three/fiber";
import { useRef } from "react";
import { useControls } from "leva";

let depthColor1 = new THREE.Color("#b34110");
let surfaceColor1 = new THREE.Color("#111210");

const StarMaterial = shaderMaterial({}, starVertexShader, starFragmentShader);

const WaveMaterial = shaderMaterial(
  {
    uTime: 0,
    uBigWavesElevation: 0,
    uBigWavesFrequency: new THREE.Vector2(0, 0),
    uBigWavesSpeed: 0.75,
    uSmallWavesElevation: 0.384,
    uSmallWavesFrequency: 12.436,
    uSmallWavesSpeed: 2.122,
    uSmallIterations: 4,
    uDepthColor: depthColor1,
    uSurfaceColor: surfaceColor1,
    uColorOffset: 0.244,
    uColorMultiplier: 5,
  },
  waveVertexShader,
  waveFragmentShader
);

//40.00789901806392, -105.27000601728639

// let lat = (90 - 40.00789901806392) * (Math.PI / 180);
// let lng = (180 + -105.27000601728639) * (Math.PI / 180);

// 28.615205932605956, 77.27145735512924

let lat = 28.615205932605956 * (Math.PI / 180);
let lng = 77.27145735512924 * (Math.PI / 180);

let x = Math.cos(lat) * Math.sin(lng);
let y = Math.sin(lat) + 0.15;
let z = Math.cos(lat) * Math.cos(lng);

extend({ StarMaterial, WaveMaterial });

function getRandom(min, max) {
  return Math.random() * (max - min) + min;
}

export default function Experience() {
  const props = useTexture({ map: "earthMap.jpg" });

  props.map.wrapS = THREE.RepeatWrapping; // You do not need to set `.wrapT` in this case
  props.map.offset.x = 1.5708 / (2 * Math.PI);

  const { radius, tube, rotation } = useControls("geometry", {
    radius: {
      value: 0.09,
      min: 0,
      max: 1,
      step: 0.01,
    },
    tube: {
      value: 0.02,
      min: 0,
      max: 0.1,
      step: 0.01,
    },
    rotation: {
      value: 0,
      min: -Math.PI * 2,
      max: Math.PI * 2,
      step: 0.1,
    },
  });

  const { color } = useControls("shader", {
    color: depthColor1,
  });

  useFrame(({ clock }) => (ref.current.uTime = clock.getElapsedTime()));

  const ref = useRef();

  return (
    <>
      <OrbitControls />

      <ambientLight intensity={0.2} />
      <directionalLight />
      <mesh>
        <sphereGeometry args={[1, 32, 32]} />
        <meshStandardMaterial {...props} />
      </mesh>
      <mesh position={[x, y, z]}>
        <sphereGeometry args={[0.05, 32, 32]} />
        <waveMaterial ref={ref} attach="material" />
      </mesh>

      {[...Array(400)].map(() => (
        <mesh
          position={[
            (Math.random() - 0.5) * 50,
            (Math.random() - 0.5) * 50,
            (Math.random() - 0.5) * 50,
          ]}
          rotation={[
            getRandom(0, Math.PI * 2),
            getRandom(0, Math.PI * 2),
            getRandom(0, Math.PI * 2),
          ]}
        >
          <sphereGeometry args={[0.1, 32, 32]} />
          <starMaterial />
        </mesh>
      ))}
    </>
  );
}
