//     uSurfaceColor: surfaceColor2,
//     uColorOffset: 0.244,
//     uColorMultiplier: 5,
//   },
//   waveVertexShader,
//   waveFragmentShader
// );

//40.00789901806392, -105.27000601728639

let lat1 = 40.00789901806392 * (Math.PI / 180);
let lng1 = -105.27000601728639 * (Math.PI / 180);

let x1 = Math.cos(lat1) * Math.sin(lng1);
let y1 = Math.sin(lat1) + 0.25;
let z1 = Math.cos(lat1) * Math.cos(lng1);

// 28.615205932605956, 77.27145735512924

let lat2 = 28.615205932605956 * (Math.PI / 180);
let lng2 = 77.27145735512924 * (Math.PI / 180);

let x2 = Math.cos(lat2) * Math.sin(lng2);
let y2 = Math.sin(lat2) + 0.25;
let z2 = Math.cos(lat2) * Math.cos(lng2);

extend({ StarMaterial, WaveMaterial1 });

function getRandom(min, max) {
  return Math.random() * (max - min) + min;
}

export default function Experience() {
  const props = useTexture({ map: "earthMap.jpg" });

  props.map.wrapS = THREE.RepeatWrapping; // You do not need to set `.wrapT` in this case
  props.map.offset.x = 1.5708 / (2 * Math.PI);

  // const { radius, tube, rotation } = useControls("geometry", {
  //   radius: {
  //     value: 0.09,
  //     min: 0,
  //     max: 1,
  //     step: 0.01,
  //   },
  //   tube: {
  //     value: 0.02,
  //     min: 0,
  //     max: 0.1,
  //     step: 0.01,
  //   },
  //   rotation: {
  //     value: 0,
  //     min: -Math.PI * 2,
  //     max: Math.PI * 2,
  //     step: 0.1,
  //   },
  // });

  const { color } = useControls("shader", {
    color: depthColor1,
  });

  useFrame(({ clock }) => (ref.current.uTime = clock.getElapsedTime()));

  const ref = useRef();

  return (
    <>
      <OrbitControls />

      <ambientLight intensity={0.2} />
      <directionalLight />
      <mesh>
        <sphereGeometry args={[1, 32, 32]} />
        <meshStandardMaterial {...props} />
      </mesh>
      <mesh position={[x1, y1, z1]}>
        <circleGeometry args={[0.1, 27]} />
        <meshBasicMaterial />
      </mesh>
      <mesh position={[x2, y2, z2]}>
        <sphereGeometry args={[0.03, 8, 8]} />
        <meshBasicMaterial />
      </mesh>

      {[...Array(400)].map(() => (
        <mesh
          position={[
            (Math.random() - 0.5) * 50,
            (Math.random() - 0.5) * 50,
            (Math.random() - 0.5) * 50,
          ]}
          rotation={[
            getRandom(0, Math.PI * 2),
            getRandom(0, Math.PI * 2),
            getRandom(0, Math.PI * 2),
          ]}
        >
          <sphereGeometry args={[0.1, 32, 32]} />
          <starMaterial />
        </mesh>
      ))}
    </>
  );
}
